commits.content:
@import '@/styles/shared.scss';

.wrapper {
  position: relative;
  display: flex;
  align-items: center;
  gap: 100px;
  padding-bottom: 90px;
  padding-top: 120px;
  z-index: 995;
}

.gradient {
  right: 0;
}

.contentWrapper {
  width: 45%;

  button {
    margin-top: 16px;
  }
}

.imageWrapper {
  width: 55%;
  position: relative;
  height: 450px;

  img {
    border-radius: 5px;
    object-fit: cover;
  }
}

@media (max-width: 1080px) {
  .wrapper {
    flex-direction: column;
    gap: 16px;
    padding-top: 90px;
  }

  .contentWrapper {
    width: 100%;
    order: 1;
  }

  .imageWrapper {
    width: 100%;
  }
}
