@import '@/styles/shared.scss';

.form {
  width: 50%;
  position: relative;
  display: flex;
  flex-direction: column;

  button {
    width: 100%;
  }

  label {
    font-size: 13px;
    font-weight: 700;
    padding: 8px 0;
  }

  input {
    padding: 12px 16px;
    outline: none;
    border-radius: 5px;
    font-size: 15px;
    color: rgba(0, 0, 0, 0.8);
    font-weight: 500;
    margin-bottom: 12px;

    &::placeholder {
      font-family: "Inter";
      font-weight: 600;
    }
  }

  textarea {
    padding: 12px 16px;
    outline: none;
    border-radius: 5px;
    font-size: 15px;
    color: rgba(0, 0, 0, 0.8);
    font-weight: 500;
    margin-bottom: 12px;
    resize: none;
    height: 120px;

    &::placeholder {
      font-family: "Inter";
      font-weight: 600;
    }
  }

  input[type="checkbox"] {
    height: 18px;
    width: 18px;
    margin: 0;
    padding: 0;
    opacity: 1;
    appearance: none;
    border-radius: 3px;
    background: #fff;
    position: absolute;
    margin-right: 10px;
    cursor: pointer;
  }

  input[type="checkbox"]:checked {
    background: #F6831F;
  }

  input[type="checkbox"]:checked:before, input[type="checkbox"]:checked:after {
    content: "";
    position: absolute;
    height: 2px;
    background: #fff;
  }

  input[type="checkbox"]:checked:before {
    width: 7px;
    top: 10px;
    left: 3px;
    transform: rotate(44deg);
    border-radius: 100px;
  }

  input[type="checkbox"]:checked:after {
    width: 10px;
    top: 8px;
    left: 6px;
    transform: rotate(-55deg);
    border-radius: 100px;
  }

  input[type="checkbox"]:focus {
    outline: none;
  }

  .invalidLabel, .invalidClause {
    color: #C30000;
  }

  .invalidInput {
    &::placeholder {
      position: absolute;
      right: 16px;
      color: #C30000;
    }
  }
}

.checkboxWrapper {
  margin: 12px 0;
  p {
    margin-left: 30px;
    color: rgba(255, 255, 255, 0.8);
    font-size: 13px;
    cursor: pointer;
    font-weight: 400;

    &::before {
      content: "*";
      position: absolute;
      transform: translateX(-5px);
      color: #C30000;
    }
  }
}

@media (max-width: 1080px){
  .form {
    width: 100%;

    input, textarea, label {
      font-size: 12px;
    }

    textarea {
      height: 75px;
    }
  }
}
